mapper.insert:
INSERT INTO USER_M
(
    USER_ID
  , USER_PW
  , USER_NM
)
VALUES
(
    #{userId}
  , #{userPw}
  , #{userNm}
)

INSERT INTO USER_M
(
  USER_ID
, USER_PW
, REGISTER_DT
, CHANGE_DT
, USER_NM
)
VALUES
    (
      #{userId}
    , #{userPw}
    , SYSDATE
    , SYSDATE
    , #{userNm}
    )

mapper.update:
UPDATE USER_M
   SET USER_NM = #{userNm}
     , CHANGE_DT = sysdate
 WHERE USER_ID = #{userId}
