mapper.insert:
INSERT INTO T_PRODUCT
(
    USER_NUM
  , PRODUCT_NM
  , REGISTER_QUANTITY
  , PRODUCT_PRICE
  , PRODUCT_STATUS_CD
  , POST_YN
  , REGISTER_DT
)
VALUES
(
    #{userNum}
  , #{productNm}
  , #{registerQuantity}
  , #{productPrice}
  , #{productStatusCd}
  , nvl(#{postYn}, 'Y')
  , now()
)

mapper.update:
UPDATE T_PRODUCT
   SET PRODUCT_NM = #{productNm}
     , PRODUCT_PRICE = #{productPrice}
     , REGISTER_QUANTITY = #{registerQuantity}
     , PRODUCT_STATUS_CD = #{productStatusCd}
     , POST_YN = #{postYn}
     , CHANGE_DT = now()
 WHERE PRODUCT_NUM = #{productNum}

mapper.insert:
INSERT INTO T_ORDERED_PRODUCT
(
    ORDER_NUM
  , PRODUCT_NUM
  , PRODUCT_PRICE
  , ORDER_QUANTITY
  , DISCOUNT_PRICE
  , FINAL_PAYMENT_PRICE
  , REGISTER_DT
)
VALUES
(
    #{orderNum}
  , #{productNum}
  , #{productPrice}
  , #{orderQuantity}
  , #{discountPrice}
  , #{finalPaymentPrice}
  , now()
)

mapper.update:
UPDATE PRODUCT_M
SET QUANTITY = #{quantity}
, PRODUCT_STTS = #{productStts}

    PRODUCT_NUM = #{productNum}

/* ProductDAO.updateProductViewCount 상품조회수 증가*/
UPDATE T_PRODUCT_VIEWS
