mapper.insert:
INSERT INTO T_PRODUCT
(
    USER_NUM
  , PRODUCT_NM
  , REGISTER_QUANTITY
  , PRODUCT_PRICE
  , PRODUCT_STATUS_CD
  , POST_YN
  , REGISTER_DT
  , CHANGE_DT
)
VALUES
(
    #{userNum}
  , #{productNm}
  , #{registerQuantity}
  , #{productPrice}
  , #{productStatusCd}
  , nvl(#{postYn}, 'Y')
  , now()
  , now()
)

mapper.update:
UPDATE T_PRODUCT
   SET PRODUCT_NM = #{productNm}
     , PRODUCT_PRICE = #{productPrice}
     , REGISTER_QUANTITY = #{registerQuantity}
     , PRODUCT_STATUS_CD = #{productStatusCd}
     , CHANGE_DT = now()
 WHERE PRODUCT_NUM = #{productNum}

