mapper.insert:
/* UserDAO.insertUser 사용자추가*/
INSERT INTO T_USER
(
    USER_ID
  , USER_PW
  , USER_NM
  , REGISTER_DT
  , CHANGE_DT
)
VALUES
(
    #{userId}
  , #{userPw}
  , #{userNm}
  , SYSDATE()
  , SYSDATE()
)

mapper.update:
/* UserDAO.updateUserInfo USER정보 업데이트*/
UPDATE T_USER
   SET USER_NM = #{userNm}
     , CHANGE_DT = SYSDATE()
 WHERE USER_ID = #{userId}
